import React, { useEffect, useRef } from 'react';
import L from 'leaflet';

const MapComponent = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);

  useEffect(() => {
    if (!mapInstanceRef.current) {
      // Initialize the map only if it's not already initialized
      mapInstanceRef.current = L.map(mapContainerRef.current, {
        center: [51.505, -0.09], // Default center position
        zoom: 13,                // Default zoom level
        maxZoom: 18,             // Optional: Set max zoom level
      });

      // Add a tile layer to the map (e.g., OpenStreetMap)
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(mapInstanceRef.current);
    }

    // Adjust map size when the window resizes
    const resizeMap = () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.invalidateSize(); // Recalculate map size
      }
    };

    window.addEventListener('resize', resizeMap);

    // Cleanup: Remove event listener and map instance on component unmount
    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove(); // Properly destroy the map
        mapInstanceRef.current = null;
      }
      window.removeEventListener('resize', resizeMap);
    };
  }, []);

  return <div ref={mapContainerRef} className="interactive-map"></div>;
};

export default MapComponent;
