import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = ({ onLocationSelect }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markerRef = useRef(null);

  useEffect(() => {
    if (!mapInstanceRef.current && mapContainerRef.current) {
      // Initial map setup with zoom and center
      mapInstanceRef.current = L.map(mapContainerRef.current, {
        center: [17.397194281352338, 78.49024767779724], // Initial coordinates (Hyderabad, Telangana)
        zoom: 15, // Zoom level
        maxZoom: 18,
      });

      // Tile layer for the map
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(mapInstanceRef.current);

      // Custom marker icon
      const customIcon = L.icon({
        iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
        shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
        iconSize: [25, 41],
        iconAnchor: [12, 41],
        popupAnchor: [1, -34],
        shadowSize: [41, 41],
      });

      // Add the marker at the initial center
      markerRef.current = L.marker([17.397194281352338, 78.49024767779724], { 
        draggable: true, 
        icon: customIcon 
      }).addTo(mapInstanceRef.current);

      // Update marker position on dragend
      markerRef.current.on('dragend', updateMarkerPosition);

      // Capture click events and move marker
      mapInstanceRef.current.on('click', function (e) {
        const { lat, lng } = e.latlng; // Get lat and lng from click event
        markerRef.current.setLatLng([lat, lng]); // Move marker to clicked location
        updateMarkerPosition(); // Update lat/lng after marker is moved
        console.log(`You clicked the map at latitude: ${lat} and longitude: ${lng}`);
      });

      // Ensure map is properly sized on window resize
      const resizeMap = () => {
        if (mapInstanceRef.current) {
          mapInstanceRef.current.invalidateSize();
        }
      };

      setTimeout(() => {
        resizeMap();
      }, 100);

      window.addEventListener('resize', resizeMap);

      return () => {
        window.removeEventListener('resize', resizeMap);
        if (mapInstanceRef.current) {
          mapInstanceRef.current.remove();
          mapInstanceRef.current = null;
        }
      };
    }
  }, [onLocationSelect]);

  // Function to update the marker's lat/lng after being dragged or moved
  const updateMarkerPosition = () => {
    if (markerRef.current) {
      const position = markerRef.current.getLatLng(); // Get marker's current lat/lng
      onLocationSelect(position.lat, position.lng); // Send lat/lng back to parent component
    }
  };

  return <div ref={mapContainerRef} className="interactive-map" style={{ height: '500px' }} />;
};

export default MapComponent;
