import React, { useState } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';

const libraries = ['drawing'];

const MapComponent = () => {
  const [map, setMap] = useState(null);
  const [drawingManager, setDrawingManager] = useState(null);

  // Keshav Memorial Junior College coordinates
  const center = {
    lat: 17.3867,
    lng: 78.4872
  };

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyB-6NuWru71NBpaRmAaiEZjRmTJnUfQBbQ',
    libraries,
  });

  const mapContainerStyle = {
    height: '500px',
    width: '100%',
  };

  // Custom map styles to remove walking options
  const mapOptions = {
    zoom: 18, // Higher zoom level for better initial view
    center: center,
    mapTypeControl: true,
    streetViewControl: false, // Disable street view
    fullscreenControl: true,
    zoomControl: true,
    styles: [
      {
        featureType: "transit.station.bus",
        stylers: [{ visibility: "off" }]
      },
      {
        featureType: "transit",
        stylers: [{ visibility: "off" }]
      }
    ]
  };

  const onLoad = React.useCallback((map) => {
    setMap(map);

    // Initialize DrawingManager with larger icons
    const drawingManagerInstance = new window.google.maps.drawing.DrawingManager({
      drawingMode: null,
      drawingControl: true,
      drawingControlOptions: {
        position: window.google.maps.ControlPosition.TOP_CENTER,
        drawingModes: [
          window.google.maps.drawing.OverlayType.RECTANGLE,
          window.google.maps.drawing.OverlayType.POLYGON,
          window.google.maps.drawing.OverlayType.CIRCLE,
        ],
        // Style the drawing control panel
        style: {
          transform: 'scale(1.5)', // Make the entire control panel bigger
          marginTop: '10px'
        }
      },
      // Style options for the drawing tools
      rectangleOptions: {
        fillColor: '#FF0000',
        fillOpacity: 0.2,
        strokeWeight: 2,
        clickable: true,
        editable: true,
        zIndex: 1,
      },
      polygonOptions: {
        fillColor: '#FF0000',
        fillOpacity: 0.2,
        strokeWeight: 2,
        clickable: true,
        editable: true,
        zIndex: 1,
      },
      circleOptions: {
        fillColor: '#FF0000',
        fillOpacity: 0.2,
        strokeWeight: 2,
        clickable: true,
        editable: true,
        zIndex: 1,
      }
    });

    // Apply custom CSS to make drawing controls bigger
    const cssStyles = `
      .gm-style-mtc button,
      .gmnoprint > div {
        transform: scale(1.3) !important;
        margin: 5px !important;
      }
    `;

    const styleSheet = document.createElement("style");
    styleSheet.innerText = cssStyles;
    document.head.appendChild(styleSheet);

    drawingManagerInstance.setMap(map);
    setDrawingManager(drawingManagerInstance);

    // Event listeners for shape completion
    window.google.maps.event.addListener(drawingManagerInstance, 'rectanglecomplete', function(rectangle) {
      const bounds = rectangle.getBounds();
      console.log('Rectangle bounds:', bounds.toJSON());
    });

    window.google.maps.event.addListener(drawingManagerInstance, 'polygoncomplete', function(polygon) {
      const coordinates = polygon.getPath().getArray().map(coord => ({
        lat: coord.lat(),
        lng: coord.lng(),
      }));
      console.log('Polygon coordinates:', coordinates);
    });

    window.google.maps.event.addListener(drawingManagerInstance, 'circlecomplete', function(circle) {
      const center = circle.getCenter();
      const radius = circle.getRadius();
      console.log('Circle center:', center.toJSON(), 'radius:', radius);
    });
  }, []);

  const onUnmount = React.useCallback((map) => {
    setMap(null);
    if (drawingManager) {
      drawingManager.setMap(null);
    }
  }, [drawingManager]);

  if (loadError) {
    return <div className="p-4 text-red-500">Error loading maps</div>;
  }

  if (!isLoaded) {
    return <div className="p-4">Loading maps...</div>;
  }

  return (
    <div className="w-full">
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        options={mapOptions}
        onLoad={onLoad}
        onUnmount={onUnmount}
      />
    </div>
  );
};

export default MapComponent;
