import React, { useState, useRef, useEffect } from 'react';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { 
  Bot, 
  Send, 
  X, 
  BatteryMedium, 
  Signal, 
  Cloud, 
  Sun, 
  CloudRain, 
  Wind, 
  Droplets, 
  ThermometerSun,
  CloudLightning,
  CloudDrizzle,
  CloudSnow,
  Cloudy
} from 'lucide-react';
import { AURA_SYSTEM_CONTEXT, generateEnhancedPrompt, getQueryType } from './AuraPrompts';
import './ChatInterface.css';

const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;
const OPENWEATHER_API_KEY = process.env.REACT_APP_OPENWEATHER_API_KEY;

const WeatherCard = ({ weather }) => {
  const getWeatherIcon = (condition) => {
    switch (condition.toLowerCase()) {
      case 'clear': 
        return <Sun color="white" size={24} />;
      case 'clouds': 
      case 'cloudy': 
        return <Cloudy color="white" size={24} />;
      case 'rain': 
        return <CloudRain color="white" size={24} />;
      case 'thunderstorm': 
        return <CloudLightning color="white" size={24} />;
      case 'drizzle': 
        return <CloudDrizzle color="white" size={24} />;
      case 'snow': 
        return <CloudSnow color="white" size={24} />;
      case 'mist':
      case 'fog':
      case 'haze':
        return <Cloud color="white" size={24} opacity={0.7} />;
      default: 
        return <Cloud color="white" size={24} />;
    }
  };

  const getWeatherAdvice = (weather) => {
    const windSpeed = parseFloat(weather.windSpeed);
    const visibility = weather.visibility || 10000;

    if (windSpeed > 15) {
      return "WARNING: Wind speeds are too high for safe drone operation.";
    }
    if (visibility < 5000) {
      return "CAUTION: Limited visibility may affect drone operations.";
    }
    if (weather.condition.toLowerCase().includes('rain') || 
        weather.condition.toLowerCase().includes('snow') ||
        weather.condition.toLowerCase().includes('thunderstorm')) {
      return "WARNING: Current weather conditions are not suitable for drone flight.";
    }
    return "Conditions are suitable for drone operations.";
  };

  return (
    <div className="weather-card">
      <div className="weather-header">
        <div className="weather-icon">
          {getWeatherIcon(weather.condition)}
        </div>
        <div className="weather-info">
          <div className="weather-temp">{weather.temperature}°C</div>
          <div className="weather-desc">{weather.condition}</div>
        </div>
      </div>
      <div className="weather-details">
        <div className="weather-detail-item">
          <Wind size={16} />
          <span>Wind: {weather.windSpeed}</span>
        </div>
        <div className="weather-detail-item">
          <Droplets size={16} />
          <span>Humidity: {weather.humidity}%</span>
        </div>
        <div className="weather-detail-item">
          <ThermometerSun size={16} />
          <span>Feels like: {weather.feelsLike}°C</span>
        </div>
      </div>
      <div className="weather-advice">
        {getWeatherAdvice(weather)}
      </div>
    </div>
  );
};

const ChatInterface = ({ droneStatus, missionData }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [apiError, setApiError] = useState(null);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  // API key validation
  useEffect(() => {
    if (!OPENWEATHER_API_KEY || !GEMINI_API_KEY) {
      setApiError('Missing API keys. Please check your configuration.');
      console.error('Missing required API keys');
      return;
    }

    // Test weather API connection
    fetchWeatherData('Hyderabad')
      .catch(error => {
        setApiError('Weather service unavailable. Please try again later.');
        console.error('Weather API test failed:', error);
      });
  }, []);

  const genAI = new GoogleGenerativeAI(GEMINI_API_KEY);

  const fetchWeatherData = async (location = "Hyderabad") => {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${OPENWEATHER_API_KEY}&units=metric`
      );
      const data = await response.json();

      if (data.cod !== 200) {
        throw new Error(data.message || 'Error fetching weather data');
      }

      return {
        temperature: Math.round(data.main.temp),
        condition: data.weather[0].main,
        windSpeed: `${data.wind.speed} km/h`,
        humidity: data.main.humidity,
        feelsLike: Math.round(data.main.feels_like),
        visibility: data.visibility,
        description: data.weather[0].description
      };
    } catch (error) {
      console.error('Error fetching weather:', error);
      throw error;
    }
  };

  useEffect(() => {
    if (messages.length === 0) {
      setMessages([{
        role: 'assistant',
        content: "Hello, I'm AURA, your AI Unified Rescue Assistant. I'm here to help with mission planning, drone operations, and emergency protocols. How can I assist you today?"
      }]);
    }
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const adjustTextareaHeight = () => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = 'auto';
      textarea.style.height = `${Math.min(textarea.scrollHeight, 150)}px`;
    }
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
    adjustTextareaHeight();
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;
    if (apiError) {
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: 'I apologize, but I am currently experiencing technical difficulties. Please try again later.',
        type: 'error'
      }]);
      return;
    }

    try {
      setIsLoading(true);
      const userMessage = { role: 'user', content: input };
      setMessages(prev => [...prev, userMessage]);

      // Check if it's a weather query
      if (input.toLowerCase().includes('weather')) {
        try {
          const locationMatch = input.match(/weather (?:in|at|for) ([\w\s]+)/i);
          const location = locationMatch ? locationMatch[1] : "Hyderabad";

          const weatherData = await fetchWeatherData(location);

          const response = {
            text: `Current weather conditions in ${location} for drone operations:
• Temperature: ${weatherData.temperature}°C
• Wind Speed: ${weatherData.windSpeed}
• Conditions: ${weatherData.condition}
• Visibility: ${(weatherData.visibility / 1000).toFixed(1)}km

${parseFloat(weatherData.windSpeed) < 10 ? 
  "Conditions are suitable for drone flight." : 
  "Warning: Wind speed may affect drone stability. Exercise caution."}

Would you like me to help plan the mission considering these conditions?`,
            weatherData: weatherData
          };

          setMessages(prev => [...prev, {
            role: 'assistant',
            content: response.text,
            type: 'weather',
            weatherData: weatherData
          }]);
        } catch (error) {
          setMessages(prev => [...prev, {
            role: 'assistant',
            content: "I'm having trouble fetching the weather data. Please verify the location and try again.",
            type: 'error'
          }]);
        }
      } else {
        const model = genAI.getGenerativeModel({ 
          model: 'gemini-pro',
          generationConfig: {
            temperature: 0.7,
            maxOutputTokens: 1000,
            topP: 0.8,
            topK: 40,
          }
        });

        const context = {
          droneStatus: {
            battery: droneStatus?.battery ?? 100,
            signalStrength: droneStatus?.signalStrength ?? 100,
            status: droneStatus?.status ?? 'Ready'
          },
          missionData: {
            areaCovered: missionData?.areaCovered ?? '0%',
            objectsDetected: missionData?.objectsDetected ?? 0,
            weatherCondition: missionData?.weatherCondition ?? 'Clear',
            windSpeed: missionData?.windSpeed ?? '0 km/h'
          },
          currentTime: new Date().toISOString()
        };

        const enhancedPrompt = generateEnhancedPrompt(input, context);

        const chat = await model.startChat({
          history: [
            { role: 'system', parts: [AURA_SYSTEM_CONTEXT] },
            ...messages.map(msg => ({
              role: msg.role,
              parts: [msg.content],
            }))
          ],
        });

        const result = await chat.sendMessage(enhancedPrompt);
        const response = await result.response;

        setMessages(prev => [...prev, { 
          role: 'assistant', 
          content: response.text(),
          type: getQueryType(input)
        }]);
      }

      if (textareaRef.current) {
        textareaRef.current.style.height = '48px';
      }
      setInput('');

    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: `I encountered an error: ${error.message}. Please try again.`,
        type: 'error'
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-icon" onClick={() => setIsOpen(!isOpen)}>
        <Bot />
      </div>

      <div className={`chat-panel ${isOpen ? 'open' : ''}`}>
        {apiError && (
          <div className="api-error-banner">
            {apiError}
          </div>
        )}
        <div className="chat-header">
          <div className="aura-logo">
            <Bot />
          </div>
          <div className="header-text">
            <h3>AURA</h3>
            <small>AI Unified Rescue Assistant</small>
          </div>
          <button className="close-button" onClick={() => setIsOpen(false)}>
            <X />
          </button>
        </div>

        <div className="status-indicators">
          <div className="status-indicator">
            <BatteryMedium size={16} />
            <span>{droneStatus.battery}%</span>
          </div>
          <div className="status-indicator">
            <Signal size={16} />
            <span>{droneStatus.signalStrength}%</span>
          </div>
          <div className="status-indicator">
            <Cloud size={16} />
            <span>{missionData.weatherCondition}</span>
          </div>
        </div>

        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role} ${message.type || ''}`}>
              {message.content}
              {message.type === 'weather' && message.weatherData && (
                <WeatherCard weather={message.weatherData} />
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        <div className="chat-input">
          <div className="input-wrapper">
            <textarea
              ref={textareaRef}
              value={input}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Ask AURA about your mission..."
              disabled={isLoading}
              rows={1}
            />
          </div>
          <button 
            className="send-button"
            onClick={handleSend} 
            disabled={isLoading || !input.trim()}
          >
            {isLoading ? (
              <div className="loading-spinner" />
            ) : (
              <Send />
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
