import React, { useState } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';

const libraries = ['drawing'];

const MapComponent = () => {
  const [map, setMap] = useState(null);
  const [drawingManager, setDrawingManager] = useState(null);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'YOUR_API_KEY_HERE',
    libraries,
  });

  const mapContainerStyle = {
    height: '500px',
    width: '100%',
  };

  const center = {
    lat: 17.397194281352338,
    lng: 78.49024767779724,
  };

  const onLoad = React.useCallback((map) => {
    setMap(map);

    // Initialize DrawingManager
    const drawingManagerInstance = new window.google.maps.drawing.DrawingManager({
      drawingMode: null,
      drawingControl: true,
      drawingControlOptions: {
        position: window.google.maps.ControlPosition.TOP_CENTER,
        drawingModes: [
          window.google.maps.drawing.OverlayType.RECTANGLE,
          window.google.maps.drawing.OverlayType.POLYGON,
          window.google.maps.drawing.OverlayType.CIRCLE,
        ],
      },
      rectangleOptions: {
        fillColor: '#FF0000',
        fillOpacity: 0.2,
        strokeWeight: 2,
        clickable: true,
        editable: true,
        zIndex: 1,
      },
    });

    drawingManagerInstance.setMap(map);
    setDrawingManager(drawingManagerInstance);

    // Add event listeners for shape completion
    window.google.maps.event.addListener(drawingManagerInstance, 'rectanglecomplete', function(rectangle) {
      const bounds = rectangle.getBounds();
      console.log('Rectangle bounds:', bounds.toJSON());
    });

    window.google.maps.event.addListener(drawingManagerInstance, 'polygoncomplete', function(polygon) {
      const coordinates = polygon.getPath().getArray().map(coord => ({
        lat: coord.lat(),
        lng: coord.lng(),
      }));
      console.log('Polygon coordinates:', coordinates);
    });

    window.google.maps.event.addListener(drawingManagerInstance, 'circlecomplete', function(circle) {
      const center = circle.getCenter();
      const radius = circle.getRadius();
      console.log('Circle center:', center.toJSON(), 'radius:', radius);
    });
  }, []);

  const onUnmount = React.useCallback((map) => {
    setMap(null);
    if (drawingManager) {
      drawingManager.setMap(null);
    }
  }, [drawingManager]);

  if (loadError) {
    return <div className="p-4 text-red-500">Error loading maps</div>;
  }

  if (!isLoaded) {
    return <div className="p-4">Loading maps...</div>;
  }

  return (
    <div className="w-full">
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={12}
        center={center}
        onLoad={onLoad}
        onUnmount={onUnmount}
      />
    </div>
  );
};

export default MapComponent;
