import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = ({ setLatitude, setLongitude }) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    // Initialize the map only once
    if (!map) {
      const leafletMap = L.map('map').setView([51.505, -0.09], 13); // Default view of the map
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(leafletMap);

      // On map click, update latitude and longitude
      leafletMap.on('click', function (e) {
        const { lat, lng } = e.latlng;
        setLatitude(lat);  // Pass latitude to the parent component
        setLongitude(lng); // Pass longitude to the parent component
      });

      setMap(leafletMap);
    }
  }, [map, setLatitude, setLongitude]);

  return <div id="map" style={{ height: '400px', width: '100%' }}></div>;
};

export default MapComponent;
