import React, { useState, useCallback } from 'react';
import { NavLink } from 'react-router-dom';
import './Dashboard.css';
import { useAuth } from './AuthContext';
import MapComponent from './components/MapComponent'; // Import MapComponent

function Dashboard() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [droneLocation, setDroneLocation] = useState('');
  const [missionStatus, setMissionStatus] = useState('');
  const { logout } = useAuth();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // State to toggle sidebar visibility

  // Handle drone dispatch based on latitude and longitude
  const handleDroneLocation = (e) => {
    e.preventDefault();
    setMissionStatus(`Drone dispatched to coordinates: ${latitude}, ${longitude}`);
    setDroneLocation(`Latitude: ${latitude}, Longitude: ${longitude}`);
  };

  // Function to handle selected map location
  const handleLocationSelect = useCallback((lat, lng) => {
    setLatitude(lat.toFixed(6));
    setLongitude(lng.toFixed(6));
  }, []);

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      alert('Logout failed: ' + error.message);
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev); // Toggle sidebar state
  };

  return (
    <div className="dashboard-container">
      <aside className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <h2>AI Drone Rescue Command Center</h2>
        <ul>
          <li>
            <NavLink to="/missioncontrol" className={({ isActive }) => (isActive ? 'active' : '')}>
              Mission Control
            </NavLink>
          </li>
          <li>
            <NavLink to="/history" className={({ isActive }) => (isActive ? 'active' : '')}>
              History
            </NavLink>
          </li>
          <li>
            <NavLink to="/alerts" className={({ isActive }) => (isActive ? 'active' : '')}>
              Alerts
            </NavLink>
          </li>
          <li>
            <NavLink to="/payloadmanagement" className={({ isActive }) => (isActive ? 'active' : '')}>
              Payload Management
            </NavLink>
          </li>
          <li>
            <NavLink to="/trainingsimulation" className={({ isActive }) => (isActive ? 'active' : '')}>
              Training & Simulation
            </NavLink>
          </li>
          <li>
            <NavLink to="/settings" className={({ isActive }) => (isActive ? 'active' : '')}>
              Settings
            </NavLink>
          </li>
        </ul>
      </aside>

      <main className={`main-content ${isSidebarOpen ? 'expanded' : ''}`}>
        <header className="header">
          <span>Welcome, Rescue Operator</span>
          <button className="logout-button" onClick={handleLogout}>Logout</button>
          <span className={`hamburger ${isSidebarOpen ? 'open' : ''}`} onClick={toggleSidebar}>
            &#9776;
          </span>
        </header>

        <section className="dashboard">
          <div className="live-feed">
            <h3>Live Drone Feed</h3>
            <div className="video-feed">Live Video Feed</div>
            <div className="feed-info">
              <span>Battery: 75%</span>
              <span>Signal: Strong</span>
              <span>Altitude: 50m</span>
            </div>
          </div>

          {/* Map Section */}
          <div className="mission-map">
            <h3>Mission Map</h3>
            <MapComponent
              onLocationSelect={handleLocationSelect}
              isInteractive={true} // Map is always interactive now
            />
            <div className="map-info">
              <span>Area Covered: 78%</span>
              <span>Objects Detected: 12</span>
            </div>
          </div>
        </section>

        <section className="drone-location">
          <h2 className="text-2xl font-semibold mb-4 text-blue-800">
            <i className="fas fa-drone mr-2"></i>Dispatch Drone
          </h2>
          <form onSubmit={handleDroneLocation}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="latitude">
                Latitude
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="latitude"
                type="text"
                value={latitude}
                onChange={(e) => setLatitude(e.target.value)}
                placeholder="Enter latitude"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="longitude">
                Longitude
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="longitude"
                type="text"
                value={longitude}
                onChange={(e) => setLongitude(e.target.value)}
                placeholder="Enter longitude"
              />
            </div>
            <div className="flex items-center justify-between">
              <button
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-110"
                type="submit"
              >
                <i className="fas fa-paper-plane mr-2"></i>Dispatch Drone
              </button>
            </div>
          </form>
          {droneLocation && (
            <div className="drone-status mt-4">
              <h3>Current Drone Location</h3>
              <p>{droneLocation}</p>
              <p>{missionStatus}</p>
            </div>
          )}
        </section>
      </main>
    </div>
  );
}

export default Dashboard;
