import React, { useEffect, useRef } from 'react';
import L from 'leaflet';

const MapComponent = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);

  useEffect(() => {
    if (!mapInstanceRef.current) {
      // Initialize the map if not already initialized
      mapInstanceRef.current = L.map(mapContainerRef.current).setView([51.505, -0.09], 13);

      // Add a tile layer to the map (e.g., OpenStreetMap)
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(mapInstanceRef.current);
    }

    // Handle map resizing and ensure it adjusts properly when the window resizes
    const resizeMap = () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.invalidateSize();
      }
    };

    window.addEventListener('resize', resizeMap);

    // Cleanup function to remove the map when the component unmounts
    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
      window.removeEventListener('resize', resizeMap);
    };
  }, []);

  return <div ref={mapContainerRef} className="interactive-map"></div>;
};

export default MapComponent;
