import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-draw/dist/leaflet.draw.css'; // Import Leaflet Draw CSS
import 'leaflet-draw'; // Import Leaflet Draw JS

const MapComponent = ({ onLocationSelect }) => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);

  useEffect(() => {
    if (!mapInstanceRef.current && mapContainerRef.current) {
      // Initialize map
      mapInstanceRef.current = L.map(mapContainerRef.current, {
        center: [17.397194281352338, 78.49024767779724], // Center at Hyderabad
        zoom: 17,
        maxZoom: 18,
      });

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(mapInstanceRef.current);

      // Initialize the Leaflet.draw control and toolbar
      const drawnItems = new L.FeatureGroup();
      mapInstanceRef.current.addLayer(drawnItems);

      const drawControl = new L.Control.Draw({
        edit: {
          featureGroup: drawnItems,
          remove: true,
        },
        draw: {
          polygon: true,
          rectangle: true,
          circle: true,
          polyline: false,
          marker: false,
        },
      });

      mapInstanceRef.current.addControl(drawControl);

      // Event listener for when a shape is created
      mapInstanceRef.current.on(L.Draw.Event.CREATED, function (event) {
        const layer = event.layer;
        drawnItems.addLayer(layer);

        // Get the shape's bounds or coordinates
        const shapeType = event.layerType;
        if (shapeType === 'polygon' || shapeType === 'rectangle') {
          const bounds = layer.getLatLngs();
          console.log('Shape Bounds:', bounds);
        } else if (shapeType === 'circle') {
          const center = layer.getLatLng();
          const radius = layer.getRadius();
          console.log('Circle Center:', center, 'Radius:', radius);
        }
      });

      return () => {
        if (mapInstanceRef.current) {
          mapInstanceRef.current.remove();
          mapInstanceRef.current = null;
        }
      };
    }
  }, [onLocationSelect]);

  return <div ref={mapContainerRef} className="interactive-map" style={{ height: '500px' }} />;
};

export default MapComponent;
