import React from 'react';
import { GoogleMap, useLoadScript, DrawingManager } from '@react-google-maps/api';

const libraries = ['drawing']; // Ensure 'drawing' is included in libraries
const mapContainerStyle = {
  height: '500px',
  width: '100%',
};

const center = { lat: 17.397194281352338, lng: 78.49024767779724 }; // Hyderabad center

const MapComponent = ({ onLocationSelect }) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyB-6NuWru71NBpaRmAaiEZjRmTJnUfQBbQ', // Replace with your actual API key
    libraries,
  });

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading maps...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      zoom={17}
      center={center}
      options={{
        disableDefaultUI: true, // Disables all default controls (including walking icon)
        zoomControl: true, // Enables zoom control only
        mapTypeControl: false, // Hides map type options
        streetViewControl: false, // Removes the street view (walking icon) control
      }}
    >
      <DrawingManager
        onPolygonComplete={(polygon) => {
          const path = polygon.getPath().getArray().map((latLng) => ({
            lat: latLng.lat(),
            lng: latLng.lng(),
          }));
          console.log('Polygon Coordinates:', path);
          polygon.setMap(null); // Clear the drawn shape if needed
        }}
        onRectangleComplete={(rectangle) => {
          const bounds = rectangle.getBounds().toJSON();
          console.log('Rectangle Bounds:', bounds);
          rectangle.setMap(null); // Clear the drawn shape if needed
        }}
        onCircleComplete={(circle) => {
          const center = circle.getCenter();
          const radius = circle.getRadius();
          console.log('Circle Center:', center.toJSON(), 'Radius:', radius);
          circle.setMap(null); // Clear the drawn shape if needed
        }}
        options={{
          drawingControl: true,
          drawingControlOptions: {
            drawingModes: ['polygon', 'rectangle', 'circle'], // Only boundary shapes allowed
          },
        }}
      />
    </GoogleMap>
  );
};

export default MapComponent;
