import React, { useState, useCallback, useRef } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';
import axios from 'axios';
import './MapComponent.css';

const libraries = ['drawing', 'places'];

const MapComponent = ({ onDispatchDrone }) => {
  const [selectedArea, setSelectedArea] = useState(null);
  const [drawingManager, setDrawingManager] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const center = {
    lat: 17.39716,
    lng: 78.49040,
  };

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyB-6NuWru71NBpaRmAaiEZjRmTJnUfQBbQ',
    libraries,
  });

  const searchBoxRef = useRef(null);
  const mapRef = useRef(null); // Reference to the map container

  const mapContainerStyle = {
    width: '100%',
    height: '100%',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  };

  const mapOptions = {
    zoom: 18,
    center: center,
    mapTypeId: 'satellite',
    mapTypeControl: false,
    streetViewControl: false,
    fullscreenControl: true,
    zoomControl: true,
  };

  const onLoad = useCallback((map) => {
    const drawingManagerInstance = new window.google.maps.drawing.DrawingManager({
      drawingMode: null,
      drawingControl: true,
      drawingControlOptions: {
        position: window.google.maps.ControlPosition.TOP_CENTER,
        drawingModes: [
          window.google.maps.drawing.OverlayType.RECTANGLE,
        ],
      },
    });

    drawingManagerInstance.setMap(map);
    setDrawingManager(drawingManagerInstance);

    window.google.maps.event.addListener(drawingManagerInstance, 'rectanglecomplete', (rectangle) => {
      const bounds = rectangle.getBounds();
      const selectedBounds = bounds.toJSON();
      setSelectedArea(selectedBounds);
    });
  }, []);

  const onUnmount = useCallback(() => {
    if (drawingManager) {
      drawingManager.setMap(null);
    }
  }, [drawingManager]);

  const handleSearch = () => {
    if (searchBoxRef.current) {
      const autocomplete = new window.google.maps.places.Autocomplete(searchBoxRef.current);
      autocomplete.setFields(['place_id', 'geometry', 'name']);

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          const { lat, lng } = place.geometry.location;
          setSelectedLocation({ lat: lat(), lng: lng() });
          // Pan to the selected location
          mapRef.current.panTo(place.geometry.location);
        } else {
          alert("No details available for the selected place.");
        }
      });
    }
  };

  const handleDispatchDrone = async () => {
    if (selectedArea) {
      try {
        const requestData = {
          top_left: {
            latitude: parseFloat(selectedArea.north).toFixed(6),
            longitude: parseFloat(selectedArea.west).toFixed(6),
          },
          bottom_right: {
            latitude: parseFloat(selectedArea.south).toFixed(6),
            longitude: parseFloat(selectedArea.east).toFixed(6),
          },
        };

        const response = await axios.post('http://172.168.0.157:3001/drone/dispatch/rectangle', requestData);
        console.log('Drone dispatched:', response.data);
      } catch (error) {
        console.error('Error dispatching drone:', error);
        alert('Failed to dispatch drone');
      }
    } else {
      alert('No area selected');
    }
  };

  if (loadError) {
    return <div className="map-error">Error loading maps</div>;
  }

  if (!isLoaded) {
    return <div className="map-loading">Loading maps...</div>;
  }

  return (
    <div className="map-wrapper">
      <input
        ref={searchBoxRef}
        type="text"
        placeholder="Search a place"
        className="search-box"
        onChange={handleSearch}  // Ensure search updates with input change
      />
      <GoogleMap
        ref={mapRef}  // Reference to the map for panning
        mapContainerStyle={mapContainerStyle}
        options={mapOptions}
        onLoad={onLoad}
        onUnmount={onUnmount}
        center={selectedLocation || center}  // Center the map on the selected location
      >
      </GoogleMap>
      {selectedArea && (
        <button onClick={handleDispatchDrone} className="dispatch-button">
          Dispatch Drone
        </button>
      )}
    </div>
  );
};

export default MapComponent;
